#!/usr/bin/env ruby
# encoding: utf-8

# <bitbar.title>Show Time machine Progress</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author>Slamet Kristanto</bitbar.author>
# <bitbar.author.github>drselump14</bitbar.author.github>
# <bitbar.desc>Show time machine backup progress</bitbar.desc>
# <bitbar.dependencies>time machine</bitbar.dependencies>

require 'date'
require 'filesize'

# runs an apple script
# https://gist.github.com/dinge/6983008
def osascript(script)
  system 'osascript', *script.split(/\n/).map { |line| ['-e', line] }.flatten
end

if ARGV.length > 0
  case ARGV[0]
  when "start"
    exec('tmutil startbackup')
  when "stop"
    exec('tmutil stopbackup')
  when "enter"
    exec('open -a /Applications/Time\ Machine.app/')
  when "prefs"
    osascript <<-END
      tell application "System Preferences"
        activate
        set the current pane to pane id "com.apple.prefs.backup"
      end tell
    END
  end
  exit
end

status_lines = `tmutil status`
status = {}
status_lines.each_line do |line|
  status[:percent] = (line.split("=")[1][2..-4].strip.to_f * 100).round() if /^ *Percent =/ =~ line
  status[:running] = (line.split("=")[1][0..-2].strip).to_i if /^ *Running =/ =~ line
  status[:stopping] = (line.split("=")[1][0..-2].strip).to_i if /^ *Stopping =/ =~ line
  status[:time_remaining] = (line.split("=")[1][0..-2].strip).to_i if /^ *TimeRemaining =/ =~ line
  status[:cur_bytes] = (line.split("=")[1][0..-2].strip).to_i if /^ *bytes =/ =~ line
  status[:total_bytes] = (line.split("=")[1][0..-2].strip).to_i if /^ *totalBytes =/ =~ line
  status[:backup_phase] = line.split("=")[1][0..-2].strip if /^ *BackupPhase =/ =~ line
end

icon_idle = "| "
icon_stopping = "| "
icon_backing_up = "| "

icon_backing_up_0p = "| "
icon_backing_up_25p = "| "
icon_backing_up_50p = "| "
icon_backing_up_75p = "| "
icon_backing_up_100p = "| "

icon_finishing = "| "

if File.exist?('/private/var/db/.TimeMachine.Results')
  last_backup = DateTime.strptime((`defaults read /private/var/db/.TimeMachine.Results BACKUP_COMPLETED_DATE`.strip), "%Y-%m-%d %H:%M:%S %z").new_offset(DateTime.now.offset).strftime("%A %B %d, %Y %l:%M %P")
else
  last_backup = `/usr/libexec/PlistBuddy -c "Print Destinations:0:SnapshotDates" /Library/Preferences/com.apple.TimeMachine.plist | tail -n 2 | head -n 1`.strip
end


########
# IDLE #
########
if status[:running] == 0
  puts icon_idle
  puts "---"
  puts "Latest Backup:"
  puts last_backup
  puts "---"
  # We call this script using __FILE__ with parameter 'start' to start the time machine backup
  puts "Back Up Now | bash='#{__FILE__}' param1='start' terminal=false"

############
# STOPPING #
############
elsif status[:stopping] == 1
  puts icon_stopping
  puts "---"
  puts "Latest Backup:"
  puts last_backup
  puts "---"
  puts "Stopping..."
else

##############
# BACKING UP #
##############
  if status[:percent] == -100
    puts icon_backing_up
    puts "---"
    puts "Preparing Backup..."
    puts "---"
    puts "Skip This Backup | bash='#{__FILE__}' param1='stop' terminal=false"
  else
    if status[:backup_phase].start_with?("Copying")
      per = status[:percent]
      if per < 25
        puts icon_backing_up_0p
      elsif per >= 25 and per < 50
        puts icon_backing_up_25p
      elsif per >= 50 and per < 75
        puts icon_backing_up_50p
      elsif per >= 75 and per < 100
        puts icon_backing_up_75p
      elsif per >= 100
        puts icon_backing_up_100p
      end
      puts "---"
      puts "Backing Up: #{per}%"
      cur_bytes = status[:cur_bytes].to_s + ' B'
      total_bytes = status[:total_bytes].to_s + ' B'
      seconds_remaining = status[:time_remaining]
      puts "#{Filesize.new(cur_bytes, Filesize::SI).pretty} of #{Filesize.new(total_bytes, Filesize::SI).pretty}"
      if seconds_remaining.nil?
        puts "Calculating time remaining..."
      else
        puts "Time remaining: #{Time.at(seconds_remaining).utc.strftime("%Hh %Mmin %Ss")}"
      end
      puts "---"
      puts "Skip This Backup | bash='#{__FILE__}' param1='stop' terminal=false"
    else

###############
# CLEANING UP #
###############
      puts icon_finishing
      puts "---"
      puts "Cleaning Up..."
      puts "---"
      puts "Skip Cleaning Up | bash='#{__FILE__}' param1='stop' terminal=false"
    end
  end
end


puts "Enter Time Machine | bash='#{__FILE__}' param1='enter' terminal=false"
puts "---"
# puts "Open Time Machine Preferences... | href='x-apple.systempreferences:com.apple.prefs.backup'"
puts "Open Time Machine Preferences... | bash='#{__FILE__}' param1='prefs' terminal=false"
